mapper.select:
select uid, nickname, name, email, password, phone, birth, profileImg, loginApi, userkey
from user
where uid = #{value}

mapper.update:
UPDATE user
SET password=#{password}
WHERE uid=#{uid} and userkey='Y'

update user
set profileImg = #{profileImg}
where uid = #{uid} and userkey='Y'

update user
set userkey='Y'
where uid=#{value}

update user
set userkey=#{userkey}
where uid=#{uid}

mapper.select:
select follower
from follow
where following = #{value}

select following
from follow
where follower = #{value}
