set title = #{title},
tripterm = #{tripterm},
keyword = #{keyword},
unveiled = #{unveiled}
where boardid = #{boardid}

update board
set favoritNum = #{favoriteNum}
where boardid = #{boardid}

mapper.select:
select count(uid)
from favorite
where boardid = #{value}

select imgid, boardid, src, rep
from img
where boardid = #{value}
